import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [sortBy, setSortBy] = useState('name');
    const [editingProduct, setEditingProduct] = useState(null);
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchProducts();
    }, []);

    useEffect(() => {
        filterAndSortProducts();
    }, [products, searchTerm, selectedCategory, sortBy]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/products');
            setProducts(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching data:', error);
            setLoading(false);
        }
    };

    const filterAndSortProducts = () => {
        let filtered = products.filter(product =>
            product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            product.description.toLowerCase().includes(searchTerm.toLowerCase())
        );

        if (selectedCategory !== 'All') {
            filtered = filtered.filter(product => product.category === selectedCategory);
        }

        filtered.sort((a, b) => {
            switch (sortBy) {
                case 'price': return a.price - b.price;
                case 'name': return a.name.localeCompare(b.name);
                case 'stock': return a.stockQuantity - b.stockQuantity;
                default: return 0;
            }
        });

        setFilteredProducts(filtered);
    };

    const handleEdit = (product) => {
        setEditingProduct({ ...product });
        setShowForm(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                await axios.delete(`http://localhost:8080/api/products/${id}`);
                fetchProducts(); // Refresh the list
                alert('Product deleted successfully!');
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingProduct.id) {
                // Update existing product
                await axios.put(`http://localhost:8080/api/products/${editingProduct.id}`, editingProduct);
            } else {
                // Create new product
                await axios.post('http://localhost:8080/api/products', editingProduct);
            }

            setShowForm(false);
            setEditingProduct(null);
            fetchProducts(); // Refresh the list
            alert('Product saved successfully!');
        } catch (error) {
            console.error('Error saving product:', error);
            alert('Error saving product');
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditingProduct(prev => ({
            ...prev,
            [name]: name === 'price' || name === 'stockQuantity' ? parseFloat(value) : value
        }));
    };

    const categories = ['All', ...new Set(products.map(p => p.category))];
    const productCategories = [...new Set(products.map(p => p.category))];

    if (loading) {
        return <div className="App">Loading products...</div>;
    }

    return (
        <div className="App">
            <h1>Project Nexus Dashboard</h1>

            <button
                onClick={() => {
                    setEditingProduct({
                        name: '',
                        description: '',
                        price: 0,
                        category: '',
                        stockQuantity: 0
                    });
                    setShowForm(true);
                }}
                className="btn-add"
            >
                + Add New Product
            </button>

            {/* Search and Filter Controls */}
            <div className="controls">
                <input
                    type="text"
                    placeholder="Search products..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />

                <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="filter-select"
                >
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))}
                </select>

                <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="sort-select"
                >
                    <option value="name">Sort by Name</option>
                    <option value="price">Sort by Price</option>
                    <option value="stock">Sort by Stock</option>
                </select>
            </div>

            {/* Edit/Create Form */}
            {showForm && (
                <div className="modal-overlay">
                    <div className="modal">
                        <h2>{editingProduct.id ? 'Edit Product' : 'Create Product'}</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>Name:</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={editingProduct.name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>Description:</label>
                                <textarea
                                    name="description"
                                    value={editingProduct.description}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>Price:</label>
                                <input
                                    type="number"
                                    step="0.01"
                                    name="price"
                                    value={editingProduct.price}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>Category:</label>
                                <select
                                    name="category"
                                    value={editingProduct.category}
                                    onChange={handleInputChange}
                                    required
                                >
                                    <option value="">Select Category</option>
                                    {productCategories.map(category => (
                                        <option key={category} value={category}>{category}</option>
                                    ))}
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Stock Quantity:</label>
                                <input
                                    type="number"
                                    name="stockQuantity"
                                    value={editingProduct.stockQuantity}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            <div className="form-actions">
                                <button type="submit">Save</button>
                                <button type="button" onClick={() => setShowForm(false)}>Cancel</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Statistics */}
            <div className="stats">
                <div className="stat-card">
                    <h3>Total Products</h3>
                    <p>{filteredProducts.length}</p>
                </div>
                <div className="stat-card">
                    <h3>Total Value</h3>
                    <p>${filteredProducts.reduce((sum, p) => sum + p.price * p.stockQuantity, 0).toFixed(2)}</p>
                </div>
                <div className="stat-card">
                    <h3>Average Price</h3>
                    <p>${(filteredProducts.reduce((sum, p) => sum + p.price, 0) / filteredProducts.length || 0).toFixed(2)}</p>
                </div>
            </div>

            {/* Products Grid */}
            <div className="products-container">
                {filteredProducts.length === 0 ? (
                    <p>No products found matching your criteria.</p>
                ) : (
                    filteredProducts.map(product => (
                        <div key={product.id} className="product-card">
                            <h3>{product.name}</h3>
                            <p className="description">{product.description}</p>
                            <p><strong>Price:</strong> ${product.price}</p>
                            <p><strong>Category:</strong> {product.category}</p>
                            <p><strong>Stock:</strong> {product.stockQuantity}</p>
                            <div className="product-actions">
                                <button
                                    className="btn-edit"
                                    onClick={() => handleEdit(product)}
                                >
                                    Edit
                                </button>
                                <button
                                    className="btn-delete"
                                    onClick={() => handleDelete(product.id)}
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

export default App;
